import React, { useState, useEffect } from "react";
import {
  Search,
  Filter,
  Egg,
  Bird,
  Drumstick,
  Star,
  Truck,
  Shield,
  Heart,
  Eye,
  ArrowRight,
  X,
  Check,
} from "lucide-react";

const ProductsSection = () => {
  const [activeCategory, setActiveCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [priceRange, setPriceRange] = useState([0, 100]);
  const [selectedFeatures, setSelectedFeatures] = useState([]);
  const [sortBy, setSortBy] = useState("featured");
  const [viewedProducts, setViewedProducts] = useState([]);

  const categories = [
    {
      id: "all",
      name: "All Products",
      icon: <ArrowRight className="w-5 h-5" />,
      count: 12,
    },
    {
      id: "eggs",
      name: "Fresh Eggs",
      icon: <Egg className="w-5 h-5" />,
      count: 4,
    },
    {
      id: "birds",
      name: "Live Birds",
      icon: <Bird className="w-5 h-5" />,
      count: 5,
    },
    {
      id: "meat",
      name: "Poultry Meat",
      icon: <Drumstick className="w-5 h-5" />,
      count: 3,
    },
  ];

  const features = [
    { id: "organic", label: "Organic" },
    { id: "free_range", label: "Free Range" },
    { id: "vaccinated", label: "Vaccinated" },
    { id: "hormone_free", label: "Hormone Free" },
    { id: "local", label: "Local Farm" },
    { id: "fresh", label: "Daily Fresh" },
  ];

  const products = [
    {
      id: 1,
      name: "Premium Brown Eggs",
      category: "eggs",
      price: 5.99,
      rating: 4.9,
      reviews: 128,
      features: ["organic", "free_range", "fresh"],
      image:
        "data:image/jpeg;base64,",
      description:
        "Farm-fresh brown eggs from free-range hens. Rich in omega-3 and vitamins.",
      delivery: "Next-day delivery available",
      stock: "In Stock",
      popular: true,
    },
    {
      id: 2,
      name: "White Leghorn Chicks",
      category: "birds",
      price: 3.49,
      rating: 4.8,
      reviews: 95,
      features: ["vaccinated", "hormone_free", "local"],
      image:
        "data:image/jpeg;base64,",
      description:
        "Healthy day-old White Leghorn chicks. Excellent egg layers.",
      delivery: "Same-day shipping",
      stock: "Limited",
      popular: true,
    },
    {
      id: 3,
      name: "Organic Chicken Breast",
      category: "meat",
      price: 12.99,
      rating: 4.7,
      reviews: 204,
      features: ["organic", "hormone_free", "local"],
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTNdY0gK1PLoGh6SDmVLS0OlLbJxR35rIF_OA&s",
      description:
        "Boneless, skinless organic chicken breast. Raised without antibiotics.",
      delivery: "2-day delivery",
      stock: "In Stock",
      popular: false,
    },
    {
      id: 4,
      name: "Free Range Eggs (30pk)",
      category: "eggs",
      price: 15.99,
      rating: 4.9,
      reviews: 167,
      features: ["free_range", "organic", "fresh"],
      image:
        "data:image/jpeg;base64,",
      description: "Large free-range eggs from happy hens. Perfect for baking.",
      delivery: "Next-day delivery",
      stock: "In Stock",
      popular: true,
    },
    {
      id: 5,
      name: "Rhode Island Red Pullets",
      category: "birds",
      price: 4.99,
      rating: 4.6,
      reviews: 78,
      features: ["vaccinated", "local"],
      image:
        "data:image/jpeg;base64,",
      description: "Robust Rhode Island Red pullets. Dual-purpose breed.",
      delivery: "Next-day shipping",
      stock: "In Stock",
      popular: false,
    },
    {
      id: 6,
      name: "Whole Organic Chicken",
      category: "meat",
      price: 24.99,
      rating: 4.8,
      reviews: 142,
      features: ["organic", "hormone_free", "free_range"],
      image:
        "data:image/jpeg;base64,",
      description:
        "Whole organic chicken. Perfect for roasting and family meals.",
      delivery: "2-day delivery",
      stock: "In Stock",
      popular: true,
    },
    {
      id: 7,
      name: "Quail Eggs (20pk)",
      category: "eggs",
      price: 8.99,
      rating: 4.5,
      reviews: 56,
      features: ["organic", "local"],
      image:
        "data:image/jpeg;base64,",
      description:
        "Delicate quail eggs packed with nutrition. Gourmet quality.",
      delivery: "Next-day delivery",
      stock: "Limited",
      popular: false,
    },
    {
      id: 8,
      name: "Heritage Turkey Poults",
      category: "birds",
      price: 9.99,
      rating: 4.7,
      reviews: 63,
      features: ["vaccinated", "hormone_free"],
      image:
        "data:image/jpeg;base64,",
      description:
        "Heritage breed turkey poults. Slow-growing for superior flavor.",
      delivery: "Special shipping required",
      stock: "Pre-order",
      popular: false,
    },
  ];

  useEffect(() => {
    let filtered = products;

    // Filter by category
    if (activeCategory !== "all") {
      filtered = filtered.filter(
        (product) => product.category === activeCategory
      );
    }

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(
        (product) =>
          product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
          product.description.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by price range
    filtered = filtered.filter(
      (product) =>
        product.price >= priceRange[0] && product.price <= priceRange[1]
    );

    // Filter by features
    if (selectedFeatures.length > 0) {
      filtered = filtered.filter((product) =>
        selectedFeatures.every((feature) => product.features.includes(feature))
      );
    }

    // Sort products
    switch (sortBy) {
      case "price_low":
        filtered.sort((a, b) => a.price - b.price);
        break;
      case "price_high":
        filtered.sort((a, b) => b.price - a.price);
        break;
      case "rating":
        filtered.sort((a, b) => b.rating - a.rating);
        break;
      case "popular":
        filtered.sort((a, b) =>
          b.popular === a.popular ? 0 : b.popular ? 1 : -1
        );
        break;
      default:
        filtered.sort((a, b) => b.popular - a.popular);
    }

    setFilteredProducts(filtered);
  }, [activeCategory, products, searchQuery, priceRange, selectedFeatures, sortBy]);

  const handleFeatureToggle = (featureId) => {
    setSelectedFeatures((prev) =>
      prev.includes(featureId)
        ? prev.filter((id) => id !== featureId)
        : [...prev, featureId]
    );
  };

  const handleViewProduct = (productId) => {
    if (!viewedProducts.includes(productId)) {
      setViewedProducts([...viewedProducts, productId]);
    }
  };

  const resetFilters = () => {
    setActiveCategory("all");
    setSearchQuery("");
    setPriceRange([0, 100]);
    setSelectedFeatures([]);
    setSortBy("featured");
  };

  return (
    <div className="min-h-screen">
      {/* Products Section */}
      <section className="py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-16">
          
          {/* Filters & Search Bar */}
          <div className="mb-12">
            <div className="bg-white rounded-2xl p-6">
              <div className="flex flex-col lg:flex-row gap-6">
                {/* Search */}
                <div className="flex-1">
                  <div className="relative">
                    <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                    <input
                      type="text"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      placeholder="Search products by name or description..."
                      className="w-full pl-12 pr-4 py-3 rounded-xl border border-gray-200 focus:outline-none focus:ring-2 focus:ring-[#f4b63c] focus:border-transparent"
                    />
                  </div>
                </div>

                {/* Sort By */}
                <div className="flex items-center gap-4">
                  <span className="text-gray-600">Sort by:</span>
                  <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="px-4 py-2 rounded-lg border border-gray-200 focus:outline-none focus:ring-2 focus:ring-[#f4b63c]"
                  >
                    <option value="featured">Featured</option>
                    <option value="popular">Most Popular</option>
                    <option value="price_low">Price: Low to High</option>
                    <option value="price_high">Price: High to Low</option>
                    <option value="rating">Highest Rated</option>
                  </select>
                </div>

                {/* Reset Filters */}
                <button
                  onClick={resetFilters}
                  className="px-4 py-2 text-gray-600 hover:text-green-400 transition-colors flex items-center"
                >
                  <X className="w-4 h-4 mr-2" />
                  Reset Filters
                </button>
              </div>

              {/* Category Filter */}
              <div className="mt-6">
                <div className="flex items-center gap-2 mb-4">
                  <Filter className="w-5 h-5 text-gray-400" />
                  <span className="font-medium">Categories</span>
                </div>
                <div className="flex flex-wrap gap-2">
                  {categories.map((category) => (
                    <button
                      key={category.id}
                      onClick={() => setActiveCategory(category.id)}
                      className={`px-4 py-2 rounded-full transition-all ${
                        activeCategory === category.id
                          ? "bg-green-400 text-white"
                          : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                      }`}
                    >
                      <span className="flex items-center gap-2">
                        {category.icon}
                        {category.name}
                        <span className="text-sm opacity-90">
                          ({category.count})
                        </span>
                      </span>
                    </button>
                  ))}
                </div>
              </div>

              {/* Features Filter */}
              <div className="mt-6">
                <div className="flex items-center gap-2 mb-4">
                  <Shield className="w-5 h-5 text-gray-400" />
                  <span className="font-medium">Features</span>
                </div>
                <div className="flex flex-wrap gap-2">
                  {features.map((feature) => (
                    <button
                      key={feature.id}
                      onClick={() => handleFeatureToggle(feature.id)}
                      className={`px-4 py-2 rounded-full transition-all flex items-center gap-2 ${
                        selectedFeatures.includes(feature.id)
                          ? "bg-amber-100 text-green-800 border border-green-300"
                          : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                      }`}
                    >
                      {selectedFeatures.includes(feature.id) && (
                        <Check className="w-4 h-4" />
                      )}
                      {feature.label}
                    </button>
                  ))}
                </div>
              </div>

              {/* Price Range */}
              <div className="mt-6">
                <div className="flex items-center justify-between mb-2">
                  <span className="font-medium">Price Range</span>
                  <span className="text-green-400 font-semibold">
                    ${priceRange[0]} - ${priceRange[1]}
                  </span>
                </div>
                <input
                  type="range"
                  min="0"
                  max="100"
                  value={priceRange[1]}
                  onChange={(e) =>
                    setPriceRange([priceRange[0], parseInt(e.target.value)])
                  }
                  className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer [&::-webkit-slider-thumb]:appearance-none [&::-webkit-slider-thumb]:h-4 [&::-webkit-slider-thumb]:w-4 [&::-webkit-slider-thumb]:rounded-full [&::-webkit-slider-thumb]:bg-[#f4b63c]"
                />
              </div>
            </div>
          </div>

          {/* Results Summary */}
          <div className="mb-8">
            <div className="flex justify-between items-center">
              <h2 className="text-2xl font-bold text-gray-900">
                {filteredProducts.length} Products Found
              </h2>
              {viewedProducts.length > 0 && (
                <div className="text-sm text-gray-600">
                  Viewed: {viewedProducts.length} products
                </div>
              )}
            </div>
          </div>

          {/* Products Grid */}
          {filteredProducts.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {filteredProducts.map((product) => (
                <div
                  key={product.id}
                  className="group bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-300 overflow-hidden border border-gray-100"
                >
                  {/* Product Image */}
                  <div className="relative h-48 overflow-hidden">
                    <img
                      src={product.image}
                      alt={product.name}
                      className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                    />

                    {/* Popular Badge */}
                    {product.popular && (
                      <div className="absolute top-3 left-3 bg-green-400 text-green-800 px-3 py-1 rounded-full text-xs font-semibold">
                        Popular
                      </div>
                    )}

                    {/* Stock Status */}
                    <div
                      className={`absolute top-3 right-3 px-3 py-1 rounded-full text-xs font-semibold ${
                        product.stock === "In Stock"
                          ? "bg-green-100 text-green-800"
                          : product.stock === "Limited"
                          ? "bg-amber-100 text-green-800"
                          : "bg-red-100 text-red-800"
                      }`}
                    >
                      {product.stock}
                    </div>
                  </div>

                  {/* Product Info */}
                  <div className="p-5">
                    {/* Category */}
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-xs font-medium text-gray-500 uppercase tracking-wider">
                        {product.category === "eggs" && "Fresh Eggs"}
                        {product.category === "birds" && "Live Birds"}
                        {product.category === "meat" && "Poultry Meat"}
                      </span>
                      <div className="flex items-center">
                        <Star className="w-4 h-4 fill-[#f4b63c] text-[#f4b63c]" />
                        <span className="ml-1 text-sm font-medium">
                          {product.rating}
                        </span>
                        <span className="ml-1 text-xs text-gray-500">
                          ({product.reviews})
                        </span>
                      </div>
                    </div>

                    {/* Name & Price */}
                    <h3 className="text-lg font-bold text-gray-900 mb-2">
                      {product.name}
                    </h3>
                    <div className="flex items-center justify-between mb-3">
                      <span className="text-2xl font-bold text-gray-900">
                        ${product.price.toFixed(2)}
                      </span>
                      {viewedProducts.includes(product.id) && (
                        <span className="text-xs text-[#f4b63c] font-medium flex items-center">
                          <Eye className="w-3 h-3 mr-1" />
                          Viewed
                        </span>
                      )}
                    </div>

                    {/* Description */}
                    <p className="text-gray-600 text-sm mb-4 line-clamp-2">
                      {product.description}
                    </p>

                    {/* Features */}
                    {/* <div className="mb-4">
                      <div className="flex flex-wrap gap-2">
                        {product.features.map((feature) => (
                          <span
                            key={feature}
                            className="px-2 py-1 text-xs bg-amber-50 text-amber-700 rounded-full"
                          >
                            {features.find((f) => f.id === feature)?.label}
                          </span>
                        ))}
                      </div>
                    </div> */}

                    {/* Delivery Info */}
                    {/* <div className="flex items-center text-sm text-gray-500 mb-4">
                      <Truck className="w-4 h-4 mr-2 text-[#f4b63c]" />
                      <span>{product.delivery}</span>
                    </div> */}

                    {/* Action Buttons */}
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleViewProduct(product.id)}
                        className="flex-1 bg-green-500 hover:bg-green-400 text-white py-2 rounded-full font-medium transition-colors flex items-center justify-center"
                      >
                        <Eye className="w-4 h-4 mr-2" />
                        View Details
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-16">
              <div className="text-gray-400 mb-4">
                No products found matching your criteria
              </div>
              <button
                onClick={resetFilters}
                className="px-6 py-3 bg-green-400 text-white rounded-lg font-medium hover:bg-[#e0a533] transition-colors"
              >
                Reset All Filters
              </button>
            </div>
          )}

          {/* Category Highlights */}
          <div className="mt-16">
            <h3 className="text-2xl font-bold text-gray-900 mb-8 text-center">
              Browse By Category
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {categories.slice(1).map((category) => (
                <div
                  key={category.id}
                  className="group relative overflow-hidden rounded-2xl bg-gradient-to-br from-green-100 to-green-100 p-8 cursor-pointer"
                  onClick={() => setActiveCategory(category.id)}
                >
                  <div className="relative z-10">
                    <div className="inline-flex p-4 rounded-2xl bg-white mb-6">
                      {category.icon}
                    </div>
                    <h4 className="text-2xl font-bold text-gray-900 mb-2">
                      {category.name}
                    </h4>
                    <p className="text-gray-600 mb-4">
                      {category.count} premium products available
                    </p>
                    <button className="flex items-center text-green-400 font-semibold">
                      Browse Collection
                      <ArrowRight className="ml-2 group-hover:translate-x-2 transition-transform" />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default ProductsSection;
